import { useState, useEffect } from "react";
import ContactsList from "./components/ContactsList";
import CreateContactForm from "./components/CreateContactForm";
import "./styles.css";

export default function App() {
  const [contacts, setContacts] = useState([]);
  const [hideForm, setHideForm] = useState(true);

  const [contactInputs, setContactInputs] = useState({
    firstName: "",
    lastName: "",
    blockContact: false,
  })


  console.log("CreateContactForm State: ", { contactInputs })

  const handleContactInputs = event => {
    console.log(
      "Inside handleContactInputs: ",
      event.target.name,
      event.target.value
    )

    const inputType = event.target.type

    const inputName = event.target.name


    if (inputType === "checkbox") {
      setContactInputs({
        ...contactInputs,
        [inputName]: event.target.checked,
      })
    } else {
      setContactInputs({
        ...contactInputs,
        [inputName]: event.target.value,
      })
    }
  }



  // [TODO] Write a useEffect to fetch contacts here...
useEffect(() => {

  fetch(`http://localhost:3030/contacts`)
  .then((res) => res.json())
  .then((newContact) => {
    console.log("inside fetch: ", newContact)

  setContacts(newContact)
  })
}, [])



  return (
    <>
      <ContactsList
        contacts={contacts}
        hideForm={hideForm}
        setHideForm={setHideForm}
      />
      <main>{!hideForm && <CreateContactForm />}</main>
    </>
  );
}
