  setContacts(newContact)
  })
}, [])



  return (
    <>
      <ContactsList
        contacts={contacts}
        hideForm={hideForm}
        setHideForm={setHideForm}
      />
      <main>{!hideForm && <CreateContactForm handleContactInputs={handleContactInputs} handleSubmit={handleSubmit}/>}</main>
    </>
  );
}
