function CreateContactForm() {
  // [TODO] Write form handlers here and POST requests here...
  const [contactInputs, setContactInputs] = useState({
    firstName: "",
    lastName: "",
    blockContact: false,
  })


  console.log("CreateContactForm State: ", { contactInputs })

  const handleContactInputs = event => {
    console.log(
      "Inside handleContactInputs: ",
      event.target.name,
      event.target.value
    )

    const inputType = event.target.type

    const inputName = event.target.name


    if (inputType === "checkbox") {
      setContactInputs({
        ...contactInputs,
        [inputName]: event.target.checked,
      })
    } else {
      setContactInputs({
        ...contactInputs,
        [inputName]: event.target.value,
      })
    }
  }

  return (
    <form className="form-stack light-shadow center contact-form">
      <h1>Create Contact</h1>
      <label htmlFor="first-name-input">First Name:</label>
      <input onChange={handleContactInputs} id="first-name-input" name="first-name-input" type="text" />
      <label htmlFor="last-name-input">Last Name:</label>
      <input onChange={handleContactInputs} id="last-name-input" name="last-name-input" type="text" />
      <label htmlFor="street-input">Street:</label>
      <input onChange={handleContactInputs} id="street-input" name="street-input" type="text" />
      <label htmlFor="city-input">City:</label>
      <input onChange={handleContactInputs} id="city-input" name="city-input" type="text" />
      <label htmlFor="post-code-input">Post Code:</label>
      <input onChange={handleContactInputs} id="post-code-input" name="post-code-input" type="text" />
      <div className="checkbox-section">
        <input onChange={handleContactInputs} id="block-checkbox" name="block-checkbox" type="checkbox" />
        <label htmlFor="block-checkbox">Block</label>
      </div>
      <div className="actions-section">
        <button className="button blue" type="submit">
          Create
        </button>
      </div>
    </form>
  );
}

export default CreateContactForm;
